import 'dart:convert';import 'dart:math';import 'package:http/http.dart' as  http;import 'package:movie_db/core/model/movie_resp.dart';import 'package:movie_db/core/model/movie_response.dart';import 'package:movie_db/core/model/movie_results.dart';class Service {  String nowPlayingUrl = 'https://api.themoviedb.org/3/movie/now_playing?api_key=befc21d948862259da6f029c54831a9c&language=en-US&page=1&region=ID';  String upComingUrl = 'https://api.themoviedb.org/3/movie/upcoming?api_key=befc21d948862259da6f029c54831a9c&language=en-US&page=1&region=ID';    Future<MovieResponse> getNowPlaying() async{    try{      print('Request : $nowPlayingUrl');      final response = await http.get(nowPlayingUrl);      if(response.statusCode == 200){        Map json = jsonDecode(response.body);        print('response : ' + json.toString());        MovieResponse movieResponse = MovieResponse.fromJson(json);        return movieResponse;      }else{        print('Failed');      }    }catch(e){      print(e.toString());    }  }  Future<MovieResults> getUpComing() async{    try{      print('Request : $upComingUrl');      final response = await http.get(upComingUrl);      if (response.statusCode == 200) {        Map json = jsonDecode(response.body);        print('response : ' +json.toString());        MovieResults movieResp = MovieResults.fromJson(json);        return movieResp;      }  else{        print('failed');      }    }catch(e){      print(e.toString());    }  }}